import React, {useState} from 'react';
import { useNavigate } from 'react-router-dom';
import PropTypes from 'prop-types';
import './Form.css';
import { API_HOST } from './constants';

function Login({ setToken }) {
    const [isSubmitted, setIsSubmitted] = useState(false);

    const navigate = useNavigate();
    const errors = {
      uname: "invalid username",
      pass: "invalid password"
    };

    const openRegisterPage = () => {
      navigate("/register");
    }


    const handleSubmit = (event) => {
      //Prevent page reload
      event.preventDefault();

      var { uname, pass } = document.forms[0];

        fetch(API_HOST + "/login", {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          'email': uname.value,
          'password': pass.value,
        })
        })
        .then(res =>
        res.json()).then(userdata => {
            if(userdata["token"] != ''){
              setToken(userdata["token"]);
              setIsSubmitted(true);
              localStorage.setItem('name', userdata["UserName"]);
              localStorage.setItem('token', userdata["token"]);
            } else {
              alert("email or password is invalid")
            }

        })
      navigate(`/`);
    };




    // Generate JSX code for error message
    const renderErrorMessage = (name) =>
      name === errorMessages.name && (
        <div className="error">{errorMessages.message}</div>
      );

    // JSX code for login form
    const renderForm = (
      <div className="form">
        <form onSubmit={handleSubmit}>
          <div className="input-container">
            <label>Email </label>
            <input type="text" name="uname" required  className="input"/>
            {renderErrorMessage("uname")}
          </div>
          <div className="input-container">
            <label>Password </label>
            <input type="password" name="pass" required  className="input"/>
            {renderErrorMessage("pass")}
          </div>
          <div className="button-container">
          <input type="submit" value="Login" className="btn_register"/>
          <button className="btn_register" onClick={openRegisterPage}>Register</button>
          </div>
        </form>
      </div>
    );

    return (
      <div className="app">
        <div className="login-form">
          <div className="title">Sign In</div>
          {isSubmitted ? <div>User is successfully logged in</div> : renderForm}
        </div>
      </div>
    );
}

Login.propTypes = {
  setToken: PropTypes.func.isRequired
};

export default Login;
