// import React from 'react';
import React, {useState} from 'react';
import './Form.css';
import { API_HOST } from './constants';
import { useNavigate } from 'react-router-dom';

function Register() {

    // States for registration
    const [Email, setEmail] = useState('');
    const [userName, setUserName] = useState('');
    const [password, setPassword] = useState('');

    // States for checking the errors
    const [submitted, setSubmitted] = useState(false);
    const [error, setError] = useState(false);

    const navigate = useNavigate();

    // Handling the name change
    const handleEmail = (e) => {
    setEmail(e.target.value);
    setSubmitted(false);
    };

    // Handling the name change
    const handleUserName = (e) => {
        setUserName(e.target.value);
        setSubmitted(false);
        };

    // Handling the password change
    const handlePassword = (e) => {
    setPassword(e.target.value);
    setSubmitted(false);
    };


    // Handling the form submission
    const handleSubmit = (e) => {
    e.preventDefault();
    if (Email === '' || userName === '' || password === '') {
        setError(true);
    } else {
        setSubmitted(true);
        setError(false);
        // try {
            fetch(API_HOST + "/register", {
              method: "POST",
              body: JSON.stringify({
                email : Email,
                user_name: userName,
                password: password,
              }),
            })
            .then((res) => res.json())
            .then((data) => {
               alert(data)
               if(data == "Registration Successful"){
                navigate("/login")
               }
            })
    }
    };

    return (
    <div className="form">
        <div>
        <h1>User Registration</h1>
        </div>

        <form onSubmit={handleSubmit}>
        {/* Labels and inputs for form data */}
        <label className="label">Email</label>
        <input onChange={handleEmail} required className="input"
            value={Email} type="text" />

        <label className="label">Username</label>
        <input onChange={handleUserName} required className="input"
            value={userName} type="text" />

        <label className="label">Password</label>
        <input onChange={handlePassword} required className="input"
            value={password} type="password" />
        <input type="submit" value="Register" className="btn_register"/>
        </form>
    </div>
    );

}

export default Register;
