import React ,{useState, useEffect} from 'react';
import './ProductsPage.css';
import Footer from './Footer';
import Product from './Product';
import { API_HOST } from './constants';

function SubscriptionArea() {

    function createProduct(product) {
        return (
          <Product
            key={product.ProductId}
            ProductId={product.ProductId}
            ProductName={product.ProductName}
            ProductPrice={product.ProductPrice}
            ProductPhotoFileName={require('./product_images/general_product_img.jpg')}
            removeProduct={removeProduct}
          />
        );
    }

    const [data, setData] = useState([]);

    useEffect(() => {
        fetch(API_HOST + "/products")
        .then((res) => res.json())
        .then((data) => {
            setData(data);
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);

    function removeProduct(productId = 0){
        console.log("id", productId)
        fetch(API_HOST + "/products/" + productId, { method: 'DELETE' })
            .then(() => {
                fetch(API_HOST + "/products")
                .then((res) => res.json())
                .then((data) => {
                    setData(data);
                    console.log("data", data)
                })
                .catch((err) => {
                    console.log(err);
                });

            });
    }

    return (

    <div>
        {data.map(createProduct)}
        <Footer /> 

    </div>

    );
}

export default SubscriptionArea;





