import React ,{useState, useEffect} from 'react';
import './SubscriptionArea.css';
import Footer from './Footer';
import Music from './Music';
import { API_HOST } from './constants';

function SubscriptionArea() {

    function createMusic(music) {
        return (
            <Music
                key={music.MusicId}
                musicId={music.MusicId}
                MusicTitle={music.title}
                Year={music.year}
                WebUrl={music.web_url}
                ArtistImage={music.image_url}
                isSubscribed = {true}
            />
        );
    }

    const [data, setData] = useState([]);

    useEffect(() => {
        fetch(API_HOST + "/music")
        .then((res) => res.json())
        .then((data) => {
            setData(data);
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);

    return (

    <div>
        <h>{localStorage.getItem('token') != null ? "Your subscription list of music" :''}</h>
        {localStorage.getItem('token') != null ? data.map(createMusic) : "Please login to see your subscription"}
        <Footer />
    </div>

    );
}

export default SubscriptionArea;
