import React ,{useState, useEffect} from 'react';
import Footer from './Footer';
import Music from './Music';
import { API_HOST } from './constants';

function QueryArea() {

    function createMusic(music) {
        return (
          <Music
            key={music.MusicId}
            MusicId={music.MusicId}
            MusicTitle={music.title}
            Year={music.year}
            WebUrl={music.web_url}
            ArtistImage={music.image_url}
            isSubscribed = {false}
          />
        );
    }

    const [data, setData] = useState([]);

    useEffect(() => {
        fetch(API_HOST + "/music", {
            method: "POST",
            body: JSON.stringify({
              ProductId: props.ProductId,
              UserId: localStorage.getItem('token')  
            }),
        })
        .then((res) => res.json())
        .then((data) => {
            setData(data);
            console.log(data)
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);



      const handleSubmit = (event) => {
        //Prevent page reload
        event.preventDefault();

        var { title, artist, year } = document.forms[0];

          fetch(API_HOST + "/login", {
          method: 'POST',
          headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            'title': title.value,
            'artist': artist.value,
            'year': year.value
          })
          })
          .then(res =>
          res.json()).then(userdata => {
              if(userdata["token"] != ''){
                setToken(userdata["token"]);
                setIsSubmitted(true);
                localStorage.setItem('name', userdata["UserName"]);
                localStorage.setItem('token', userdata["token"]);
              } else {
                alert("Could not login")
              }

          })
        navigate(`/`);
      };

      const renderForm = (
        <div className="form">
          <form onSubmit={handleSubmit}>
            <div className="input-container">
              <label>Title </label>
              <input type="text" name="title"   className="input"/>
            </div>
            <div className="input-container">
              <label>Artist </label>
              <input type="text" name="artist"   className="input"/>
            </div>
            <div className="input-container">
              <label>Year </label>
              <input type="text" name="year"   className="input"/>
            </div>
            <div className="button-container">
            <input type="submit" className="btn_register"/>
            </div>
          </form>
        </div>
      );

    return (

        <div className="app">
        <div className="login-form">
          <div className="title">Sign In</div>
          {isSubmitted ? data.map(createMusic) : renderForm}
        </div>
      </div>
        // <div>
        //     <div>
        //         <div>Music based on your query</div>
        //         {data.map(createMusic)}
        //     </div>
        //     <Footer />
        // </div>

        );

}

export default QueryArea;
