import React from 'react';
import { useState } from 'react';
import { API_HOST } from './constants';

function Music(props) {
    const [addedFlag, setaddedFlag] = useState(false)
    function addToCart(){
        setaddedFlag(true)
        fetch(API_HOST + "/musicuser", {
              method: "POST",
              body: JSON.stringify({
                MusicId: props.MusicId,
                UserId: localStorage.getItem('token')  // Gets user id from local storage token
              }),
            });
    }

    if(localStorage.getItem('token') != null){
        fetch(API_HOST + "/musicusercheck/" + localStorage.getItem('token') + "/" + props.MusicId)
        .then((res) => res.json())
        .then((data) => {
            console.log(data.isAdded == "True")
            setTimeout(()=>{
                if(data.isAdded == "True"){
                    setaddedFlag(true)
                }
            }, 100)
        
        })
    }


    return (

    <div>
        <div className="music">
            <div>
                {/* <img className="circle-img" src={props.ArtistImage} alt="" /> */}
                <div className="name">Title : {props.MusicTitle}</div>
            </div>
            <div>
                <p className="info">Year : {props.Year}</p>
                {/* <p className="info">{props.key}</p>; */}

                {/* { localStorage.getItem("role") == 'customer'  || localStorage.getItem("role") == 'member' ? <button className="add_to_cart_btn" style={{padding:10}} onClick={addedFlag ? null :addToCart}>{addedFlag ? "Added" : "Add to Cart"}</button> : ""} */}
                 {/* <button id ={props.MusicId} className='remove_button' style={{marginLeft:10, padding:5}} onClick={(e) =>{props.removeMusic(e.target.id)}}> Remove </button> : "" */}
            </div>
        </div>
    </div>

    );
}


function displayMessage(){
    alert("Music Message")
}
export default Music;
