import React from 'react';
import { useState } from 'react';
import { API_HOST } from './constants';

function Music(props) {
    const [addedFlag, setaddedFlag] = useState(false)
    function subscribe(musicId){
        setaddedFlag(true)
        console.log(musicId)
        console.log(localStorage.getItem('token'))
        fetch(API_HOST + "/musicuser", {
              method: "POST",
              body: JSON.stringify({
                MusicId: musicId,
                UserId: localStorage.getItem('token')  // Gets user id from local storage token
              }),
            });
    }

    if(localStorage.getItem('token') != null){
        fetch(API_HOST + "/musicusercheck/" + localStorage.getItem('token') + "/" + props.MusicId)
        .then((res) => res.json())
        .then((data) => {
            console.log(data.isAdded == "True")
            setTimeout(()=>{
                if(data.isAdded == "True"){
                    setaddedFlag(true)
                }
            }, 100)
        
        })
    }


    return (

    <div>
        <div className="music">
            <div>
                {/* <img className="circle-img" src={props.ArtistImage} alt="" /> */}
                <div className="info">Title : {props.MusicTitle}</div>
            </div>
            <div>
                <div className="info">Year : {props.Year}</div>
                <button id ={props.MusicId} className='subscribe_button' style={{marginLeft:10, padding:5}} onClick={(e) =>{handleSubscription(e.target.id)}}> {props.isSubscribed ? "Unsubscribe": "Subscribe"} </button>
                <br></br>
            </div>
        </div>
    </div>

    );
}


function displayMessage(){
    alert("Music Message")
}
export default Music;
