import { useState } from 'react';

export default function useToken() {
  const getToken = () => {
    var tokenString = ''
    if(localStorage.getItem('token') != "undefined"){
      tokenString = localStorage.getItem('token');
      var userToken = JSON.parse(tokenString);
    }
    console.log(typeof tokenString === "undefined")
    console.log(tokenString)
    console.log(tokenString == undefined)
    console.log(tokenString == null)
    console.log(tokenString == '')
    console.log("localStorage.getItem('token')", localStorage.getItem('token'))
    console.log("localStorage.getItem('token') == ", localStorage.getItem('token') != 'undefined')
    // const userToken = JSON.parse(tokenString);
    return userToken?.token
  };

  const [token, setToken] = useState(getToken());

  const saveToken = (userToken) => {
    sessionStorage.setItem('token', JSON.stringify(userToken));
    setToken(userToken);
  };

  return {
    setToken: saveToken,
    token : sessionStorage.getItem('token')
  }
}
