import React ,{useState, useEffect} from 'react';
import Footer from './Footer';
import { API_HOST } from './constants';

function QueryArea() {

    function createMusic(music) {
        return (
          <Music
            key={music.musicId}
            musicId={music.musicId}
            MusicTitle={music.MusicTitle}
            Year={music.Year}
            ArtistImage={require('./images/general_music_img.jpg')}
            removeMusic={removeMusic}
          />
        );
    }

    const [data, setData] = useState([]);

    useEffect(() => {
        fetch(API_HOST + "/allmusic")
        .then((res) => res.json())
        .then((data) => {
            setData(data);
        })
        .catch((err) => {
            console.log(err);
        });
    }, []);

    return (

        <div>
            <div>
                {data.map(createMusic)}
            </div>
            <Footer />
        </div>

        );

}

export default QueryArea;
